function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",()=>{t.classList.toggle("active"),e.classList.toggle("active"),c.classList.toggle("locked")}),window.addEventListener("resize",()=>{991.98<window.innerWidth&&(t.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked"))})}burgerMenu();